mapper.select:
SELECT SUM(sale_number) 
FROM trade_finish 
WHERE goods_id = #{goodsIdTotal} 
AND time BETWEEN #{startTimeTotal} AND #{endTimeTotal}

SELECT SUM(consume_money) 
FROM trade_finish 
WHERE goods_id = #{goodsIdTotal} 
AND time BETWEEN #{startTimeTotal} AND #{endTimeTotal}

mapper.update:
update trade_finish
set 
finish_type = #{finishType,jdbcType=VARCHAR}
where sale_id = #{saleId,jdbcType=INTEGER}

mapper.select:
